"use client";

import { Badge } from "@/components/ui/badge";
import { Button } from "@/components/ui/button";

interface SponsorTier {
  name: string;
  price: number;
  description: string;
  perks: string[];
  popular?: boolean;
  isHighlight?: boolean;
  isFlexible?: boolean;
  isOneTime?: boolean;
}

interface SponsorTiersProps {
  tiers: SponsorTier[];
  onSponsor: (tierName: string) => void;
  selectedTier?: string | null;
  onTierSelect: (tierName: string) => void;
  isPending?: boolean;
  className?: string;
}

export function SponsorTiers({
  tiers,
  onSponsor,
  selectedTier,
  onTierSelect,
  isPending = false,
  className,
}: SponsorTiersProps) {
  return (
    <div className={className}>
      <div className="space-y-8 max-w-6xl mx-auto">
        <div className="grid gap-6 grid-cols-1 md:grid-cols-2 lg:grid-cols-3">
          {tiers.map((tier) => (
            <button
              key={tier.name}
              type="button"
              className={`relative flex h-full w-full flex-col justify-between rounded-lg border p-6 text-sm hover:border-primary/50 transition-all duration-200 group cursor-pointer ${
                selectedTier === tier.name
                  ? "bg-primary/5 border-primary ring-2 ring-primary/20"
                  : tier.isHighlight
                    ? "bg-gradient-to-br from-primary/5 to-blue-500/5 border-primary/30"
                    : "bg-card hover:bg-card/80 border-border"
              }`}
              onClick={() => onTierSelect(tier.name)}
              aria-label={`Select ${tier.name} sponsorship tier`}
            >
              {tier.popular && (
                <Badge className="absolute -top-3 left-1/2 -translate-x-1/2 bg-primary text-primary-foreground border-0">
                  Most Popular
                </Badge>
              )}
              {tier.isHighlight && (
                <Badge className="absolute -top-3 left-1/2 -translate-x-1/2 bg-gradient-to-r from-primary to-blue-500 text-white border-0">
                  Recommended
                </Badge>
              )}

              <div className="space-y-4">
                <div className="text-center">
                  <h3 className="text-xl font-semibold">{tier.name}</h3>
                </div>

                <div className="space-y-3 text-center">
                  <div className="text-3xl font-bold">
                    ${tier.price}
                    <span className="text-lg font-normal text-muted-foreground">
                      +
                    </span>
                    <div className="text-sm font-normal text-muted-foreground mt-1">
                      {tier.isOneTime ? "minimum/once" : "minimum/mo"}
                    </div>
                  </div>
                  <p className="text-sm text-muted-foreground leading-relaxed">
                    {tier.description}
                  </p>
                </div>

                <ul className="space-y-3 text-sm">
                  {tier.perks.map((perk, index) => (
                    <li
                      key={`${tier.name}-perk-${index}`}
                      className="flex items-start gap-3"
                    >
                      <svg
                        role="img"
                        aria-label="Checkmark"
                        className="size-4 text-primary mt-0.5 flex-shrink-0"
                        fill="none"
                        viewBox="0 0 24 24"
                        stroke="currentColor"
                      >
                        <path
                          strokeLinecap="round"
                          strokeLinejoin="round"
                          strokeWidth={2}
                          d="M5 13l4 4L19 7"
                        />
                      </svg>
                      <span>{perk}</span>
                    </li>
                  ))}
                </ul>
              </div>
            </button>
          ))}
        </div>

        <div className="text-center space-y-4">
          <Button
            size="lg"
            className="h-12 px-8 gap-2"
            disabled={!selectedTier || isPending}
            onClick={() => selectedTier && onSponsor(selectedTier)}
          >
            {isPending
              ? "Creating checkout..."
              : selectedTier
                ? `Continue with ${selectedTier}`
                : "Select a tier"}
          </Button>
          <p className="text-sm text-muted-foreground">
            Need a custom tier?{" "}
            <a href="#contact" className="underline hover:text-foreground">
              Contact us
            </a>
          </p>
        </div>
      </div>
    </div>
  );
}

import { Checkout } from "@polar-sh/nextjs";

export const GET = Checkout({
  accessToken: process.env.POLAR_ACCESS_TOKEN || "",
  successUrl: process.env.POLAR_SUCCESS_URL || "",
  server: "production", // Use "sandbox" for testing - change to "production" when ready
  theme: "dark",
});

"use client";

import { useState, useTransition } from "react";

import { SponsorTiers } from "@/components/elements/polar/sponsor-tiers";

// Example Polar Product IDs - Replace with your own product IDs
const EXAMPLE_POLAR_PRODUCT_IDS = {
  "Tip Jar": "714b5098-71bd-43a3-b704-b776ab9b57d3",
  Supporter: "a80fbd28-adb3-48e2-9dba-ca7d19919abb",
  Sponsor: "99ba3a23-8056-4532-a0ea-f378d40ac178",
  Backer: "8bf45d94-2972-4bd4-b745-cd34b3bdc629",
};

interface SponsorTier {
  name: string;
  price: number;
  description: string;
  perks: string[];
  popular?: boolean;
  isHighlight?: boolean;
  isFlexible?: boolean;
  isOneTime?: boolean;
}

interface SponsorshipPageProps {
  title?: string;
  subtitle?: string;
  tiers: SponsorTier[];
  productIds?: Record<string, string>;
  className?: string;
  children?: React.ReactNode;
}

export function SponsorshipPage({
  title = "Sponsor This Project",
  subtitle = "Support open source development",
  tiers,
  productIds,
  className,
  children,
}: SponsorshipPageProps) {
  const [selectedTier, setSelectedTier] = useState<string | null>(null);
  const [isPending, startTransition] = useTransition();

  const handleSponsor = (tierName: string) => {
    startTransition(() => {
      try {
        // Use provided product IDs or fall back to examples
        const productIdsToUse = productIds || EXAMPLE_POLAR_PRODUCT_IDS;
        const productId = productIdsToUse[tierName as keyof typeof productIdsToUse];

        if (!productId) {
          throw new Error(`No product ID found for tier: ${tierName}`);
        }

        // Use the Polar NextJS adapter checkout route
        const checkoutUrl = `/api/checkout?products=${productId}`;
        window.location.href = checkoutUrl;
      } catch (error) {
        console.error("Failed to create checkout:", error);
        alert("Failed to create checkout. Please try again.");
      }
    });
  };

  return (
    <div className={className}>
      {/* Hero Section */}
      <div className="relative overflow-hidden">
        <div className="w-full pt-12 pb-8 px-4">
          <div className="text-center max-w-4xl mx-auto space-y-6">
            <div className="space-y-4">
              <h1 className="text-3xl md:text-4xl lg:text-5xl font-bold tracking-tight">
                {title}
              </h1>
              <p className="text-muted-foreground text-lg max-w-2xl mx-auto">
                {subtitle}
              </p>
            </div>
          </div>
        </div>
      </div>

      {/* Custom content */}
      {children}

      {/* Sponsor Tiers */}
      <div id="sponsor-tiers" className="w-full px-4 py-8">
        <SponsorTiers
          tiers={tiers}
          onSponsor={handleSponsor}
          selectedTier={selectedTier}
          onTierSelect={setSelectedTier}
          isPending={isPending}
        />
      </div>
    </div>
  );
}

"use client";

import { useRouter, useSearchParams } from "next/navigation";
import { useEffect, useState } from "react";

import { Badge } from "@/components/ui/badge";
import { Button } from "@/components/ui/button";

interface CheckoutData {
  id: string;
  status: string;
  amount: number;
  currency: string;
  product_name?: string;
  customer_email?: string;
}

export default function SuccessPage() {
  const searchParams = useSearchParams();
  const router = useRouter();
  const [checkoutData, setCheckoutData] = useState<CheckoutData | null>(null);
  const [isLoading, setIsLoading] = useState(true);

  const checkoutId = searchParams.get("checkout_id");

  useEffect(() => {
    if (checkoutId) {
      // You can optionally fetch checkout details from Polar API here
      // For now, we'll just show a generic success message
      setCheckoutData({
        id: checkoutId,
        status: "completed",
        amount: 0,
        currency: "USD",
      });
    }
    setIsLoading(false);
  }, [checkoutId]);

  if (isLoading) {
    return (
      <div className="min-h-screen flex items-center justify-center">
        <div className="text-center space-y-4">
          <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-primary mx-auto"></div>
          <p className="text-muted-foreground">
            Processing your sponsorship...
          </p>
        </div>
      </div>
    );
  }

  if (!checkoutId) {
    return (
      <div className="min-h-screen flex items-center justify-center">
        <div className="text-center space-y-6 max-w-md">
          <div className="space-y-2">
            <h1 className="text-2xl font-bold text-destructive">
              Invalid Access
            </h1>
            <p className="text-muted-foreground">
              This page can only be accessed after a successful sponsorship
              checkout.
            </p>
          </div>
          <Button onClick={() => router.push("/sponsor")}>
            Go to Sponsorship Page
          </Button>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen flex items-center justify-center px-4">
      <div className="text-center space-y-8 max-w-2xl mx-auto">
        {/* Success Icon */}
        <div className="mx-auto flex items-center justify-center w-20 h-20 bg-primary/10 rounded-full">
          <svg
            className="w-10 h-10 text-primary"
            fill="none"
            stroke="currentColor"
            viewBox="0 0 24 24"
            aria-label="Success checkmark"
            role="img"
          >
            <path
              strokeLinecap="round"
              strokeLinejoin="round"
              strokeWidth={2}
              d="M5 13l4 4L19 7"
            />
          </svg>
        </div>

        {/* Success Message */}
        <div className="space-y-4">
          <h1 className="text-3xl md:text-4xl font-bold">Thank You!</h1>
          <p className="text-xl text-muted-foreground">
            Your sponsorship means the world to us!
          </p>
          <p className="text-muted-foreground max-w-lg mx-auto">
            We've received your sponsorship and you'll get a confirmation email
            shortly. Your support helps us continue building amazing tools.
          </p>
        </div>

        {/* Checkout Details */}
        {checkoutData && (
          <div className="bg-card border rounded-lg p-6 text-left">
            <h3 className="font-semibold mb-4">Sponsorship Details</h3>
            <div className="space-y-3 text-sm">
              <div className="flex justify-between items-center">
                <span className="text-muted-foreground">Checkout ID:</span>
                <Badge variant="outline" className="font-mono text-xs">
                  {checkoutData.id}
                </Badge>
              </div>
              <div className="flex justify-between items-center">
                <span className="text-muted-foreground">Status:</span>
                <Badge variant="default" className="text-xs">
                  {checkoutData.status}
                </Badge>
              </div>
            </div>
          </div>
        )}

        {/* What's Next */}
        <div className="space-y-4">
          <h3 className="text-lg font-semibold">What's Next?</h3>
          <div className="text-sm text-muted-foreground space-y-3">
            <div className="flex items-start gap-3">
              <svg
                className="size-4 text-primary mt-0.5 flex-shrink-0"
                fill="none"
                viewBox="0 0 24 24"
                stroke="currentColor"
              >
                <title>Question Icon</title>
                <path
                  strokeLinecap="round"
                  strokeLinejoin="round"
                  strokeWidth={2}
                  d="M3 8l7.89 7.89a2 2 0 002.83 0L21 8M5 19h14a2 2 0 002-2V7a2 2 0 00-2-2H5a2 2 0 00-2 2v10a2 2 0 002 2z"
                />
              </svg>
              <span>Check your email for a receipt and confirmation</span>
            </div>
            <div className="flex items-start gap-3">
              <svg
                className="size-4 text-primary mt-0.5 flex-shrink-0"
                fill="none"
                viewBox="0 0 24 24"
                stroke="currentColor"
              >
                <title>Question Icon</title>
                <path
                  strokeLinecap="round"
                  strokeLinejoin="round"
                  strokeWidth={2}
                  d="M13 10V3L4 14h7v7l9-11h-7z"
                />
              </svg>
              <span>You'll be added to our sponsors page within 24 hours</span>
            </div>
            <div className="flex items-start gap-3">
              <svg
                className="size-4 text-primary mt-0.5 flex-shrink-0"
                fill="none"
                viewBox="0 0 24 24"
                stroke="currentColor"
              >
                <title>Question Icon</title>
                <path
                  strokeLinecap="round"
                  strokeLinejoin="round"
                  strokeWidth={2}
                  d="M8 12h.01M12 12h.01M16 12h.01M21 12c0 4.418-4.03 8-9 8a9.863 9.863 0 01-4.255-.949L3 20l1.395-3.72C3.512 15.042 3 13.574 3 12c0-4.418 4.03-8 9-8s9 3.582 9 8z"
                />
              </svg>
              <span>Feel free to reach out if you have any questions</span>
            </div>
          </div>
        </div>

        {/* Action Buttons */}
        <div className="flex flex-col sm:flex-row gap-4 justify-center">
          <Button onClick={() => router.push("/")} variant="default">
            Back to Home
          </Button>
          <Button onClick={() => router.push("/sponsor")} variant="outline">
            View Sponsorship Page
          </Button>
        </div>

        {/* Contact */}
        <p className="text-sm text-muted-foreground">
          Questions?{" "}
          <a href="#contact" className="underline hover:text-foreground">
            Contact us
          </a>
        </p>
      </div>
    </div>
  );
}
